import React, { Component } from "react";
import logo from './logo.svg';
import Container from "./components/Container";
import Nav from "./components/Nav";
import Header from "./components/Header";
import data from "./data.json";
import ClickItem from "./components/ClickItem";


class App extends Component {
  state = { 
    data,
    score: 1,
    topScore: 10,
  };

  gameOver = () => {
    if (this.state.score > this.state.topScore) {
      this.setState({topScore: this.state.score}, function() {
        console.log(this.state.topScore);
      });
    }
    this.state.data.forEach(card => {
      card.count = 0;
    });
    alert(`Game Over :( \nscore: ${this.state.score}`);
    this.setState({score: 0});
    return true;
  }

  clickCount = id => {
    this.state.cards.find((o, i) => {
      if (o.id === id) {
        if(data[i].count === 0){
          data[i].count = data[i].count + 1;
          this.setState({score : this.state.score + 1}, function(){
            console.log(this.state.score);
          });
          this.state.data.sort(() => Math.random() - 0.5)
          return true; 
        } else {
          this.gameOver();
        }
      }
    });
  }

  handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  render() {
    return (
      <Container>
        <Nav />
        <Header score={this.state.score} topScore={this.state.topScore}></Header>
        {this.state.data.map(clickItem => (
          <ClickItem
            clickCount={this.clickCount}
            id={clickItem.id}
            key={clickItem.id}
            image={clickItem.image}
          />
        ))}
      </Container>  
    );
  }
}

export default App;
